import{_ as e,c as r,o as a,ag as s}from"./chunks/framework.i-TFNq8v.js";const p=JSON.parse('{"title":"为什么选择VitePress","description":"VitePress相比其他文档工具的优势","frontmatter":{"title":"为什么选择VitePress","description":"VitePress相比其他文档工具的优势"},"headers":[],"relativePath":"introduction/why-vitepress.md","filePath":"introduction/why-vitepress.md"}'),i={name:"introduction/why-vitepress.md"};function o(d,t,n,l,h,u){return a(),r("div",null,t[0]||(t[0]=[s('<h1 id="为什么选择vitepress" tabindex="-1">为什么选择VitePress？ <a class="header-anchor" href="#为什么选择vitepress" aria-label="Permalink to &quot;为什么选择VitePress？&quot;">​</a></h1><p>在众多静态站点生成器中，VitePress凭借其卓越的性能和用户体验脱颖而出。以下是选择VitePress的几个主要原因：</p><h2 id="极速的开发体验" tabindex="-1">极速的开发体验 <a class="header-anchor" href="#极速的开发体验" aria-label="Permalink to &quot;极速的开发体验&quot;">​</a></h2><p>VitePress基于Vite构建，具有以下优势：</p><ul><li><strong>快速的冷启动</strong>：开发服务器几乎立即启动</li><li><strong>即时的热更新</strong>：修改内容后，页面几乎立即更新，无需等待</li><li><strong>按需编译</strong>：只编译当前页面所需的内容，提高开发效率</li></ul><h2 id="简洁的开发体验" tabindex="-1">简洁的开发体验 <a class="header-anchor" href="#简洁的开发体验" aria-label="Permalink to &quot;简洁的开发体验&quot;">​</a></h2><p>VitePress专注于提供简单直观的API：</p><ul><li><strong>基于文件系统的路由</strong>：无需复杂配置，文件结构即路由结构</li><li><strong>Frontmatter配置</strong>：在Markdown文件头部通过YAML配置页面属性</li><li><strong>最小配置</strong>：默认值设计合理，大多数情况下只需少量配置</li></ul><h2 id="vue驱动的灵活性" tabindex="-1">Vue驱动的灵活性 <a class="header-anchor" href="#vue驱动的灵活性" aria-label="Permalink to &quot;Vue驱动的灵活性&quot;">​</a></h2><p>作为Vue.js官方团队的产品，VitePress提供了强大的Vue集成：</p><ul><li><strong>在Markdown中使用Vue组件</strong></li><li><strong>访问Vue生态系统</strong></li><li><strong>利用Vue的响应式特性</strong></li></ul><h2 id="优化的生产构建" tabindex="-1">优化的生产构建 <a class="header-anchor" href="#优化的生产构建" aria-label="Permalink to &quot;优化的生产构建&quot;">​</a></h2><ul><li><strong>自动代码分割</strong>：更快的首屏加载时间</li><li><strong>预渲染</strong>：SEO友好的静态HTML输出</li><li><strong>自动资源优化</strong>：CSS和JavaScript的最小化和分块</li></ul><h2 id="与其他工具的对比" tabindex="-1">与其他工具的对比 <a class="header-anchor" href="#与其他工具的对比" aria-label="Permalink to &quot;与其他工具的对比&quot;">​</a></h2><table tabindex="0"><thead><tr><th>特性</th><th>VitePress</th><th>Docusaurus</th><th>GitBook</th></tr></thead><tbody><tr><td>构建速度</td><td>⭐⭐⭐⭐⭐</td><td>⭐⭐⭐</td><td>⭐⭐</td></tr><tr><td>开发体验</td><td>⭐⭐⭐⭐⭐</td><td>⭐⭐⭐⭐</td><td>⭐⭐⭐</td></tr><tr><td>配置简易度</td><td>⭐⭐⭐⭐⭐</td><td>⭐⭐⭐</td><td>⭐⭐⭐⭐</td></tr><tr><td>自定义能力</td><td>⭐⭐⭐⭐</td><td>⭐⭐⭐⭐⭐</td><td>⭐⭐</td></tr><tr><td>内容专注度</td><td>⭐⭐⭐⭐⭐</td><td>⭐⭐⭐⭐</td><td>⭐⭐⭐⭐</td></tr></tbody></table>',15)]))}const g=e(i,[["render",o]]);export{p as __pageData,g as default};
